from flask_sqlalchemy import SQLAlchemyfrom flask import Flaskapp = Flask(__name__)app.config['SQLALCHEMY_DATABASE_URI'] = 'postgresql://sql:123@10.59.17.33:5432/sql'app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = Falselink = SQLAlchemy(app)class Waf_log(link.Model):    __tablename__ = "waf"    no = link.Column(link.INTEGER, autoincrement=True, primary_key=True)    user_agent = link.Column(link.VARCHAR)    time = link.Column(link.TIMESTAMP)    remote_ip = link.Column(link.VARCHAR)    host = link.Column(link.VARCHAR)    ip = link.Column(link.VARCHAR)    method = link.Column(link.VARCHAR)    status = link.Column(link.VARCHAR)    request_uri = link.Column(link.VARCHAR)    uri = link.Column(link.VARCHAR)    referer = link.Column(link.VARCHAR)    waf_log = link.Column(link.VARCHAR)    cookie = link.Column(link.VARCHAR)    posts = link.Column(link.VARCHAR)    args = link.Column(link.VARCHAR)    headers = link.Column(link.VARCHAR)